import { useEffect } from "react";
import { useParams, Route, useRouteMatch, Link } from "react-router-dom";

import Comments from "./../components/comments/Comments";
import HighlightedQuote from "./../components/quotes/HighlightedQuote";
import { getSingleQuote } from "../lib/api";
import useHttp from "./../hooks/use-http";
import LoadingSpinner from "../components/UI/LoadingSpinner";

const DUMMY_DATA = [
  { id: "q1", author: "Max", text: "Learning react is fun!" },
  { id: "q2", author: "Muneer", text: "Learning react is great!" },
];
const QuoteDetail = () => {
  const { sendRequest, status, data, error } = useHttp(getSingleQuote, true);

  const params = useParams();
  const match = useRouteMatch();
  const quote = DUMMY_DATA.find((quote) => quote.id === params.id);

  useEffect(() => {
    sendRequest(params.id);
  }, [sendRequest]);

  if (!data) {
    return <p>No data found</p>;
  }

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  if (error) {
    return <p className="centered focused">{error}</p>;
  }

  return (
    <div>
      <HighlightedQuote text={quote.text} author={quote.author} />
      <Route path={match.path} exact>
        <div className="centered">
          <Link className="btn--flat" to={`${match.url}/comments`}>
            Load Comments
          </Link>
        </div>
      </Route>
      <Route path={`${match.path}/comments`}>
        <Comments></Comments>
      </Route>
    </div>
  );
};

export default QuoteDetail;
