import { Link } from "react-router-dom";
import { useParams, Route, useRouteMatch } from "react-router-dom";
import Comments from "./../components/comments/Comments";
import HighlightedQuote from "./../components/quotes/HighlightedQuote";
const DUMMY_DATA = [
  { id: "q1", author: "Max", text: "Learning react is fun!" },
  { id: "q2", author: "Muneer", text: "Learning react is great!" },
];
const QuoteDetail = () => {
  const params = useParams();
  const match = useRouteMatch();
  const quote = DUMMY_DATA.find((quote) => quote.id === params.id);
  if (!quote) {
    return <p>No data found</p>;
  }
  return (
    <div>
      <HighlightedQuote text={quote.text} author={quote.author} />
      <Route path={`/quotes/${params.id}`} exact>
        <div className="centered">
          <Link className="btn--flat" to={`/quotes/${params.id}/comments`}>
            Load Comments
          </Link>
        </div>
      </Route>
      <Route path={`${match}/comments`}>
        <Comments></Comments>
      </Route>
    </div>
  );
};

export default QuoteDetail;
